import React, { useState } from 'react';
import { X, ZoomIn, ChevronLeft, ChevronRight } from 'lucide-react';

interface GalleryImage {
  id: number;
  title: string;
  description: string;
  category: string;
  color: string;
  gradient: string;
}

const images: GalleryImage[] = [
  { 
    id: 1, 
    title: 'Fresh Irish Potatoes', 
    description: 'Premium quality potatoes harvested daily from Jos Plateau',
    category: 'Products',
    color: '#8B4513',
    gradient: 'from-amber-900 to-amber-700'
  },
  { 
    id: 2, 
    title: 'Jos Plateau Farm', 
    description: 'Our sustainable farming practices on the fertile Jos Plateau',
    category: 'Farm',
    color: '#2d5016',
    gradient: 'from-green-900 to-green-700'
  },
  { 
    id: 3, 
    title: 'Quality Sorting', 
    description: 'Careful selection ensuring only the best potatoes reach you',
    category: 'Process',
    color: '#a8d08d',
    gradient: 'from-green-400 to-green-600'
  },
  { 
    id: 4, 
    title: 'Bulk Packaging', 
    description: 'Professional packaging for wholesale and retail distribution',
    category: 'Packaging',
    color: '#6B8E23',
    gradient: 'from-lime-700 to-lime-900'
  },
  { 
    id: 5, 
    title: 'Farm Harvest', 
    description: 'Peak harvest season bringing the freshest produce',
    category: 'Farm',
    color: '#556B2F',
    gradient: 'from-green-800 to-green-600'
  },
  { 
    id: 6, 
    title: 'Premium Selection', 
    description: 'Hand-picked potatoes meeting strict quality standards',
    category: 'Products',
    color: '#8FBC8F',
    gradient: 'from-green-300 to-green-500'
  },
  { 
    id: 7, 
    title: 'Storage Facility', 
    description: 'Climate-controlled storage maintaining optimal freshness',
    category: 'Facility',
    color: '#4a5f3a',
    gradient: 'from-slate-700 to-green-800'
  },
  { 
    id: 8, 
    title: 'Distribution Ready', 
    description: 'Ready for delivery across Nigeria',
    category: 'Packaging',
    color: '#7d8f69',
    gradient: 'from-green-600 to-green-400'
  },
  { 
    id: 9, 
    title: 'Organic Farming', 
    description: 'Chemical-free cultivation for healthier produce',
    category: 'Farm',
    color: '#3d5a2c',
    gradient: 'from-green-900 to-lime-700'
  }
];

const categories = ['All', 'Products', 'Farm', 'Process', 'Packaging', 'Facility'];

const GalleryPage: React.FC = () => {
  const [selected, setSelected] = useState<GalleryImage | null>(null);
  const [filter, setFilter] = useState<string>('All');
  const [hoveredId, setHoveredId] = useState<number | null>(null);

  const filteredImages = filter === 'All' 
    ? images 
    : images.filter(img => img.category === filter);

  const handleNext = () => {
    if (!selected) return;
    const currentIndex = filteredImages.findIndex(img => img.id === selected.id);
    const nextIndex = (currentIndex + 1) % filteredImages.length;
    setSelected(filteredImages[nextIndex]);
  };

  const handlePrev = () => {
    if (!selected) return;
    const currentIndex = filteredImages.findIndex(img => img.id === selected.id);
    const prevIndex = currentIndex === 0 ? filteredImages.length - 1 : currentIndex - 1;
    setSelected(filteredImages[prevIndex]);
  };

  return (
    <section id="gallery" className="py-16 bg-gradient-to-b from-gray-50 to-white min-h-screen">
      <div className="container mx-auto px-4 max-w-7xl">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-5xl md:text-6xl font-bold mb-4 bg-gradient-to-r from-green-800 to-green-600 bg-clip-text text-transparent">
            Photo Gallery
          </h1>
          <p className="text-gray-600 text-lg max-w-2xl mx-auto">
            Explore our journey from farm to table. Quality potatoes grown with care on the Jos Plateau.
          </p>
        </div>

        {/* Filter Tabs */}
        <div className="flex flex-wrap justify-center gap-3 mb-12">
          {categories.map(cat => (
            <button
              key={cat}
              onClick={() => setFilter(cat)}
              className={`px-6 py-2.5 rounded-full font-medium transition-all duration-300 ${
                filter === cat
                  ? 'bg-green-700 text-white shadow-lg transform scale-105'
                  : 'bg-white text-gray-700 hover:bg-green-50 hover:text-green-700 shadow-md'
              }`}
            >
              {cat}
            </button>
          ))}
        </div>

        {/* Gallery Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 md:gap-8">
          {filteredImages.map(img => (
            <div
              key={img.id}
              className="relative h-80 rounded-2xl shadow-xl overflow-hidden cursor-pointer transform transition-all duration-500 hover:scale-105 hover:shadow-2xl group"
              style={{ backgroundColor: img.color }}
              onClick={() => setSelected(img)}
              onMouseEnter={() => setHoveredId(img.id)}
              onMouseLeave={() => setHoveredId(null)}
            >
              {/* Gradient Overlay */}
              <div className={`absolute inset-0 bg-gradient-to-br ${img.gradient} opacity-90`}></div>

              {/* Category Badge */}
              <div className="absolute top-4 left-4 z-10">
                <span className="bg-white/90 backdrop-blur-sm text-gray-800 px-3 py-1 rounded-full text-xs font-semibold shadow-md">
                  {img.category}
                </span>
              </div>

              {/* Zoom Icon */}
              <div className={`absolute top-4 right-4 z-10 bg-white/90 backdrop-blur-sm p-2 rounded-full transition-all duration-300 ${
                hoveredId === img.id ? 'opacity-100 scale-100' : 'opacity-0 scale-75'
              }`}>
                <ZoomIn className="w-5 h-5 text-gray-700" />
              </div>

              {/* Content Overlay */}
              <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex flex-col justify-end p-6">
                <h3 className="text-white text-2xl font-bold mb-2 transform translate-y-4 group-hover:translate-y-0 transition-transform duration-300">
                  {img.title}
                </h3>
                <p className="text-white/90 text-sm transform translate-y-4 group-hover:translate-y-0 transition-transform duration-300 delay-75">
                  {img.description}
                </p>
              </div>

              {/* Simple Title (visible on mobile/non-hover) */}
              <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/70 to-transparent p-4 lg:opacity-0 group-hover:opacity-0">
                <h3 className="text-white text-xl font-semibold">{img.title}</h3>
              </div>
            </div>
          ))}
        </div>

        {/* Empty State */}
        {filteredImages.length === 0 && (
          <div className="text-center py-20">
            <p className="text-gray-500 text-xl">No images found in this category.</p>
          </div>
        )}

        {/* Lightbox Modal */}
        {selected && (
          <div 
            className="fixed inset-0 bg-black/95 flex items-center justify-center z-50 p-4 backdrop-blur-sm"
            onClick={() => setSelected(null)}
          >
            {/* Close Button */}
            <button 
              className="absolute top-6 right-6 text-white hover:text-green-400 transition-colors z-50 bg-white/10 hover:bg-white/20 rounded-full p-2"
              onClick={(e) => {
                e.stopPropagation();
                setSelected(null);
              }}
            >
              <X className="w-8 h-8" />
            </button>

            {/* Navigation Buttons */}
            {filteredImages.length > 1 && (
              <>
                <button
                  className="absolute left-6 top-1/2 -translate-y-1/2 text-white hover:text-green-400 transition-colors bg-white/10 hover:bg-white/20 rounded-full p-3 z-50"
                  onClick={(e) => {
                    e.stopPropagation();
                    handlePrev();
                  }}
                >
                  <ChevronLeft className="w-8 h-8" />
                </button>
                <button
                  className="absolute right-6 top-1/2 -translate-y-1/2 text-white hover:text-green-400 transition-colors bg-white/10 hover:bg-white/20 rounded-full p-3 z-50"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleNext();
                  }}
                >
                  <ChevronRight className="w-8 h-8" />
                </button>
              </>
            )}

            {/* Modal Content */}
            <div 
              className="relative max-w-4xl w-full"
              onClick={(e) => e.stopPropagation()}
            >
              {/* Image Card */}
              <div 
                className={`relative h-96 md:h-[500px] rounded-2xl shadow-2xl overflow-hidden bg-gradient-to-br ${selected.gradient}`}
              >
                <div className="absolute inset-0 flex items-center justify-center p-8">
                  <div className="text-center">
                    <div className="bg-white/95 backdrop-blur-sm rounded-2xl p-8 max-w-2xl shadow-xl">
                      <span className="inline-block bg-green-100 text-green-800 px-4 py-1 rounded-full text-sm font-semibold mb-4">
                        {selected.category}
                      </span>
                      <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-4">
                        {selected.title}
                      </h2>
                      <p className="text-gray-600 text-lg md:text-xl leading-relaxed">
                        {selected.description}
                      </p>
                    </div>
                  </div>
                </div>
              </div>

              {/* Image Counter */}
              <div className="text-center mt-4">
                <span className="text-white/80 text-sm">
                  {filteredImages.findIndex(img => img.id === selected.id) + 1} / {filteredImages.length}
                </span>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default GalleryPage;
